mapper.insert:
insert into sys_dept (id, name, level, 
  seq, remark, parent_id, 
  operator, operate_time, operate_ip
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{level,jdbcType=VARCHAR}, 
  #{seq,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}, 
  #{operator,jdbcType=VARCHAR}, #{operateTime,jdbcType=DATE}, #{operateIp,jdbcType=VARCHAR}
  )

update.foreach:
update sys_dept
set level = #{sysDept.level}
where id = #{sysDept.id}

mapper.select:
select count(1)
from sys_dept
where name = #{name}
and parent_id = #{parentId}

  and id != #{id}
